import React, {useEffect, useState} from 'react';

function Hello() {
    const [hellos, setHellos] = useState(['hello', 'Nǐn hǎo','হ্যালো।', 'Bonjour', 'مرحبًا', 'Hola', 'Olá', 'नमस्ते। ', 'Dzień dobry','Chào chị', 'สวัสดี']);
    const [helloIndex, setHelloIndex] = useState(0);
    const [currentHello, setCurrentHello] = useState('fahima');

    const texts = ['Text 1', 'Text 2', 'Text 3'];
  const interval = 2000; // 2 seconds

    const dispayNextHello = () => {
        setHelloIndex((prevIndex) => (prevIndex + 1));
    } 

    useEffect(()=> {
        const intervalId = setInterval(dispayNextHello, 150);

    return () => {clearInterval(intervalId);};

    },[setHelloIndex])


    return (
        <div>
            <h1 id = "hellos">{hellos[helloIndex]}</h1>
            <TextSlider texts={texts} interval={interval} />
        </div>
    );
}

export default Hello;
