import React, { useEffect, useState, useRef } from 'react';
import '../../assets/css/profilePage.css';

function InfiniteNameScroll(props) {

    const scrollString = props.scrollString;
    const[xaxis1, setXaxis1] = useState(-50);
    const[xaxis2, setXaxis2] = useState(-50);
    const slidingName1 =  useRef(null);
    const slidingName2 =useRef(null);

    useEffect(()=>{
        const setXaxis = ()=>{
            setXaxis1((prevCount)=>{prevCount - 0.025})
            setXaxis2((prevCount)=>{prevCount - 0.025})
        }

        slidingName1.current = document.getElementsByClassName('name1')[0];
        slidingName2.current = document.getElementsByClassName('name2')[0];
        const intervalId = setInterval(setXaxis, 0.125);
    },[])

    const renderString = ()=>{
        if((window.innerWidth - slidingName1.current.getBoundingClientRect().right) > 30){
            setXaxis2(-100);
        }
        else if((window.innerWidth - slidingName2.current.getBoundingClientRect().right) > 30){
            setXaxis1(-100);
        }
    };

    return (
        <div>
            {renderString}
            <div className = "name-scroll name1" style={{transform: `translate(${xaxis}%, 0%) translate3d(0px, 0px, 0px)`}}>{scrollString}</div>
            <div className = "name-scroll name2" style={{transform: `translate(${xaxis}%, 0%) translate3d(0px, 0px, 0px)`}}>{scrollString}</div>
        </div>
    );
}

export default InfiniteNameScroll;
