import React, { useEffect , useState, useRef} from 'react';
import '../../assets/css/helper.css';

function BouncyButton(props) {

    const [buttonPosition, setButtonPosition] = useState({ x: 0, y: 0 });
    const buttonMouseHovered = useRef(false);
    const buttonElement = useRef(null);
    const buttonCenter = useRef({x:0, y:0});

    useEffect(()=>{
        buttonElement.current = document.getElementsByClassName('headerButton')[0];
        let rect = buttonElement.current.getBoundingClientRect();
        buttonCenter.current.x = (rect.width/2) + rect.left;
        buttonCenter.current.y = (rect.height/2) + rect.top;

    }, [])
    const handleMouseMove =(event)=>{
        if(buttonMouseHovered.current){
            const { clientX, clientY } = event;
            setButtonPosition({x : (clientX - buttonCenter.current.x), y : clientY - buttonCenter.current.y -20}) 
        }
    }

    const bounceTheButton = ()=>{
        //center to top 
        //top to center 
        //center to bottom
        //bottom to center
    }

    const handleMouseEnter = ()=>{
        buttonMouseHovered.current = true;
    }

    //this will get the button from the edges to center
    // const getToCenter =()=>{
    //     let i =10; // parts to come to center
    //     let xFactor = buttonPosition.x/10.0;
    //     let YFactor = buttonPosition.y/10.0;
    //     const bounceInterval = setInterval(()=>{
    //         console.log(buttonPosition.x - xFactor, buttonPosition.y - xFactor);
    //         setButtonPosition({x :buttonPosition.x - xFactor,  y: buttonPosition.y - xFactor});
    //         i = i-1;
    //         if(i === 0){
    //             clearInterval(bounceInterval);
    //             // setButtonPosition({x : 0, y : 0});
    //         }
    //     }, 100)
    // }

    const getToCenter = () => {
        let i = 10; // parts to come to center
        let xFactor = buttonPosition.x / 10.0;
        let yFactor = buttonPosition.y / 10.0;
        const bounceInterval = setInterval(() => {
          console.log(buttonPosition.x - xFactor, buttonPosition.y - yFactor);
          setButtonPosition({
            x: buttonPosition.x - xFactor,
            y: buttonPosition.y - yFactor,
          });
          i = i - 1;
          if (i === 0) {
            clearInterval(bounceInterval);
            // setButtonPosition({x: 0, y: 0});
          }
        }, 100);
      };

    const handleMouseLeave= ()=>{
        let EdgeValues = {x: buttonPosition.x, y: buttonPosition.y}
        console.log('edges', EdgeValues);
        buttonMouseHovered.current = false;
        getToCenter();
        // setButtonPosition({x : 0, y : 0});
        // ButtonMove((EdgeValues.x/2)*-1, (EdgeValues.y/2)*-1, false, true);
    }

    return (
        <div>
            <button className = "headerButton" onMouseMove={handleMouseMove} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave} >
                <div className = "innerButton"style={{transform: `translate(${buttonPosition.x}px, ${buttonPosition.y}px) rotate(0.001deg)`}}>fahima</div>
            </button>   
        </div>
    );
}

export default BouncyButton;
