            console.log();
            setScrollPos((prev)=> prev+1)
        }

        window.addEventListener('scroll', scrollAction)
    }, [])
    return (
        <div>
             <div style={{width:'100px', height:'400px'}}>space</div>
             <h1 style={{transform: `translateX(${window.scrollY / 90}px`}}>belloooooooo</h1>
             <div style={{width:'100px', height:'800px'}}>space</div>

        </div>
    );
}

