import React, { useEffect, useState, useRef} from 'react';
import "../../assets/css/workItems.css"
import htmlImage from "../../assets/css/images/html.png"
import cssImage from "../../assets/css/images/css.png"
import bootstrapImage from "../../assets/css/images/bootstrap.png"
import jsImage from "../../assets/css/images/js.png"
import reactImage from "../../assets/css/images/react.png"

import sql from "../../assets/css/images/mysql.png"
import java from "../../assets/css/images/java.png"
import spring from "../../assets/css/images/springboot.png"
import aws from "../../assets/css/images/aws.png"

const initialCurve = 50;

function calculateCurve(targetElement) {
  let topPos = targetElement.getBoundingClientRect().top;
  let eleHeight = targetElement.getBoundingClientRect().height;
  let conversionRate = (eleHeight - 50) / initialCurve;

  return initialCurve + (topPos / conversionRate);
}

function scrollPage() {
    window.scrollBy(0, 8);
  }

function TechStacks() {
    const scrollDirectionChanged = useRef(false);
    const prevScrollPosition = useRef(0);
    const [xaxisScroll1, setXaxisScroll1] =  useState(0);
    const [xaxisScroll2, setXaxisScroll2] =  useState(0);
    const [curveSize, setCurveSize] = useState(50);

    const isVisibleRef = useRef(false);
    const containerRef = useRef(null);
    const curvedContainer = useRef(null);


    const [scrollTimer, setScrollTimer] = useState(null);

    useEffect(() => {
        console.log(curveSize, "curveSize")
        if(curveSize < 0){
        clearInterval(scrollTimer);
        setScrollTimer(null);
        }
        else if(curveSize < 50){
        if(scrollTimer === null){
            setScrollTimer(setInterval(scrollPage, 1))
        }
        }
    }, [curveSize])


    useEffect(()=>{
        const observer = new IntersectionObserver((entries) => {
        entries.forEach((entry) => {
            if (entry.isIntersecting) {
                isVisibleRef.current = true;
            } else {
                isVisibleRef.current = false;
            }
          });
        });

        const techStacksOnScroll = ()=>{
            let currentScrollPostion = window.scrollY;
            console.log("isVisible", isVisibleRef.current);
            if(isVisibleRef.current){
                if(currentScrollPostion > prevScrollPosition.current)
                {
                    setXaxisScroll1((prev)=>prev+0.25);
                    setXaxisScroll2((prev)=>prev-0.25);
                }
                else{
                    setXaxisScroll1((prev)=>prev-0.25);
                    setXaxisScroll2((prev)=>prev+0.25);
                }
                prevScrollPosition.current =  currentScrollPostion;

                setCurveSize(calculateCurve(curvedContainer.current));
            }
        }


        window.addEventListener('scroll', techStacksOnScroll);
        let row1 = document.getElementsByClassName('row-1')[0];
        let row2 = document.getElementsByClassName('row-2')[0];

        curvedContainer.current = document.getElementsByClassName('curved-container')[0]
        if (containerRef.current) {
            observer.observe(containerRef.current);
        }

        return () => {
            window.removeEventListener('scroll', techStacksOnScroll);
            if (containerRef.current) {
                observer.unobserve(containerRef.current);
            }
          };
    },[])
    return (

    <div style={{backgroundColor: "green", height:"100vh"}}>
        {/* <div className = "delSpace"></div> */}
        <div ref={containerRef} className={`tech-stack-header lets-work-on ${isVisibleRef.current? 'is-visible' : ''}`}> we can help you with</div>
        {/* <div className = "delSpace"></div> */}
        {/* <div className = "delSpace">spaceee</div>
        <div className = "delSpace">spaceee</div> */}
        {/* <hr></hr> */}
        <div className='parent container curved-container'>

            <div className = "row-1 row justify-content-around h-50" style={{left: "0px", transform: `translate(${xaxisScroll1}px, 0px) translate3d(0px, 0px, 0px)`}}>
                <div className = "image-background col-sm-12 col-md-2 align-top" style={{backgroundColor : ""}}>
                    <img className = "imageThing img-fluid"  src={htmlImage}></img>
                </div>
                <div className = "image-background col-sm-12 col-md-2">
                    <img className = "imageThing img-fluid"  src={cssImage}></img>
                </div>
                <div className = "image-background col-sm-12 col-md-2">
                    <img className = "imageThing img-fluid"  src={bootstrapImage}></img>
                </div>
                <div className = "image-background col-sm-12 col-md-2">
                    <img className = "imageThing img-fluid"  src={reactImage}></img>
                </div>
                <div className = "image-background col-sm-12 col-md-2">
                    <img className = "imageThing img-fluid"  src={jsImage}></img>
                </div>
            </div>
            <hr style={{color: "white"}}></hr>
            <div ref={containerRef} className = "row-2 row justify-content-around h-50 align-bottom" style={{left: "0px", transform: `translate(${xaxisScroll2}px, 0px) translate3d(0px, 0px, 0px)`}}>
                <div className = "image-background col-sm-12 col-md-2">
                        <img className = "imageThing img-fluid"  src={sql}></img>
                    </div>
                    <div className = "image-background col-sm-12 col-md-2">
                        <img className = "imageThing img-fluid"  src={java}></img>
                    </div>
                    <div className = "image-background col-sm-12 col-md-2">
                        <img className = "imageThing img-fluid"  src={spring}></img>
                    </div>
                    <div className = "image-background col-sm-12 col-md-2">
                        <img className = "imageThing img-fluid"  src={aws}></img>
                    </div>
                    <div className = "image-background col-sm-12 col-md-2">
                        <img className = "imageThing img-fluid"  src={reactImage}></img>
                    </div>
                </div>
            </div>
            <div style={{backgroundColor: "black"}}>
                <div className='curved-container curved-frame' style={{borderRadius:`0 0 ${curveSize}% ${curveSize}%`, backgroundColor: "white"}} />
            </div>

        {/* <hr></hr> */}
        {/* <div className = "delSpace"></div> */}
    </div>
    );
}

export default TechStacks;
