import React, {useRef, useEffect, useState } from 'react';
import TrueTextColorChange from '../helper/TrueTextColorChange';
import '../../assets/css/profilePage.css'

function AboutMe(props) {

    const buttonNameRef = useRef(null);
    const isVisible = useRef(0);
    const [growingDiv, setGrowingDiv] = useState(500);

    const observer = new IntersectionObserver((entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            isVisible.current = true;
          } else {
            isVisible.current = false;
          }
        });
      });


    useEffect(()=>{

      const scrollHandler = ()=>{
        if(isVisible.current){
          // console.log("growwww", growingDiv)
          setGrowingDiv((prev) => prev+10)
        }
      }

      window.addEventListener('scroll', scrollHandler);

      if (buttonNameRef.current) {
          observer.observe(buttonNameRef.current);
      }

        return () => {
          window.removeEventListener('scroll', scrollHandler);
        if (buttonNameRef.current) {
            observer.unobserve(buttonNameRef.current);

        }
        };

    },[])
    return (
        <div className={`parent-tag ${isVisible.current ? 'change-the-background' : ''}`}>
            <div className={`quote-1 ${isVisible.current ? 'change-the-background-quote' : ''}`}> Every solution leads to a betterment</div>
            <div className= 'space-small'></div>
            <div className='parent-fixed-section'>
              <div className= {`growing-section ${isVisible.current ? 'fixed2' : ''}`} style ={{height : `${100}vh`}}>
                {/* <div className={`fixed-section ${isVisible.current ? 'fixed2' : ''}`}> */}
                  <TrueTextColorChange/>
                {/* </div> */}
              </div>
              <div className='bottom-element'></div>
            </div>
           <hr ref={buttonNameRef} style={{ width: "10px", height: "10px", border: "10px", color:"red"}}></hr>
           {console.log(growingDiv)}
        </div>
    );
}

export default AboutMe;
