import React, { useState, useEffect, useRef } from 'react';
import '../../assets/css/helper.css'

function TrueTextColorChange() {
    const [scrollPos, setSetScrollPos] = useState(0);
    const [boxWidth, setBoxWidth] = useState(0);
    const lines = ["my name is fahima farook", "all the glitters isnt gold", "some ppl dont have common sense", "so thanks for it"]
    const tranlateValues = [0,0,0,0]
    const currentLine = useRef(0);

    useEffect(()=>{
        const scrollAction = ()=>{
            tranlateValues[currentLine.current.current]= tranlateValues[currentLine.current] + 1;
            // console.log(tranlateValues[currentLine.current], currentLine.current, squareBoxs[currentLine.current].getBoundingClientRect().left, rows[currentLine.current].getBoundingClientRect().right);
            if(squareBoxs[currentLine.current].getBoundingClientRect().left >= rows[currentLine.current].getBoundingClientRect().right)
            {
                currentLine.current = currentLine.current + 1;

            }
            setSetScrollPos((prev) => prev+1);
            setBoxWidth((prev) => prev-1);
        }

        window.addEventListener('scroll', scrollAction)
        const rows = document.getElementsByClassName("twins1");
        const squareBoxs = document.getElementsByClassName("the-square");
        Array.from(rows).forEach((element, index)=>{
            let box = squareBoxs[index]
            box.style.width = `${element.getBoundingClientRect().width}px`;
            box.style.height = `${element.getBoundingClientRect().height}px`;
        })

    return () => {
        window.removeEventListener('scroll', scrollAction);
        };

    }, [])
    return (
        <div className='container-fluid'>

          {lines.map((element, index) => (
            <div className = "single-row row">
                <h1 className ="twins1">{element}</h1>
                <div className= "the-square" style={{transform: `translateX(${tranlateValues[index]}px`}}>
                    <h1 className ="twins2" style={{transform: `translateX(${-(tranlateValues[index])}px`}}>{element}</h1>
                </div>
            </div>

))}


        </div>
    );
}

export default TrueTextColorChange;
