function Hello() {
    const [hellos, setHellos] = useState(['hello', 'Nǐn hǎo', 'Bonjour', 'Hola', 'Olá']);
    const [currentHello, setCurrentHello] = useState('');

    const displayAll = () =>{
        setHellos(hellos.map((item) => item));
    }
    return (
        <div>
            <button onClick={displayAll}>click</button>
            <div>{currentHello}</div>
        </div>
    );
}

export default Hello;
