import React, { useState, useEffect } from 'react';
import '../../assets/css/helper.css'

function TrueTextColorChange() {
    const [scrollPos, setSetScrollPos] = useState(0);
    const [boxWidth, setBoxWidth] = useState(0);

    useEffect(()=>{
        const scrollAction = ()=>{
            setSetScrollPos((prev) => prev+1);
            setBoxWidth((prev) => prev-1);
        }

        window.addEventListener('scroll', scrollAction)
        const letters = document.getElementsByClassName("twins1")[0];
        const squareBox = document.getElementsByClassName("the-square")[0];
        squareBox.style.width = `${letters.getBoundingClientRect().width}px`;
        squareBox.style.height = `${letters.getBoundingClientRect().height}px`;


        console.log("here");

    return () => {
        window.removeEventListener('scroll', scrollAction);
        };

    }, [])
    return (
        <div className='container'>
            <div className = "single-row">
                <h1 className ="twins1" >its me fahima farook im a devloper ..... hehe</h1>
                <div className= "the-square" style={{transform: `translateX(${window.scrollY*2}px`, width:`${boxWidth}px` }}>
                    <h1 className ="twins2" style={{transform: `translateX(${-(window.scrollY*2)}px`}}>its me fahima farook im a devloper ..... hehe</h1>
                </div>
            </div>
        </div>
    );
}

export default TrueTextColorChange;
