import React, { useEffect, useRef, useState } from 'react';
import '../../assets/css/profilePage.css';
import '../../assets/css/helper.css';
import { useNavigate } from 'react-router-dom';

function Headerbar() {
    cont[headerbar, setHeaderbar] = useState(true);
    cont[headerIcon, setHeaderIcon] = useState(false);
    const headerTopValue =  useRef(null);

    const handleScroll = () => {
        if(headerTopValue.current < -20)
            setHeaderbar(false);
        else 
            setHeaderIcon(true);
      };

    useEffect(()=>{
        window.addEventListener('scroll', handleScroll);
        headerTopValue.current = document.getElementsByClassName('headerButtonContainer')[0];

    return () => {
        window.removeEventListener('scroll', handleScroll);
    };

    },[])
    const navigate = useNavigate();
            const handleRedirect = (page) => {
            switch(page){
            case 'workPage':
                navigate('/work');
                break;
            case 'aboutPage':
                navigate('/about');
                break;
            case 'contactPage':
                navigate('/contact');
                break;
            }
    };

    return (
        <div>
        <div className = "headerButtonContainer">
            <button className = "headerButton" onClick={()=>handleRedirect("workPage")}>Work</button>
            <button className = "headerButton" onClick={()=>handleRedirect("aboutPage")}>About</button>
            <button className = "headerButton" onClick={()=>handleRedirect("contactPage")}>Contact</button>
        </div>
        </div>
    );
}

export default Headerbar;
