import '../../assets/css/profilePage.css';

function InfiniteNameScroll(props) {
    const[leftValue, setLeftValue] = useState(0);
    const[xaxis1, setXaxis1] = useState(0);
    const screenWidth = useRef(0);

    useEffect(()=>{
        const setXaxis =()=>{
            setXaxis1((prevCount)=>prevCount - 0.25)
            let thresholdDistance = screenWidth.current*0.1
            console.log(screenWidth.current, nameElemet.getBoundingClientRect().right, thresholdDistance)
            if((screenWidth.current - nameElemet.getBoundingClientRect().right) > thresholdDistance)
            {
                console.log('away from edge',nameElemet.getBoundingClientRect().left);
                setLeftValue(window.innerWidth);
                setXaxis1(0);
            }
        }

        const intervalId = setInterval(setXaxis, 0.5);
