            <div className='space'></div>
            <div className="name-scroll name1" style={{ left: `${leftValue}px`, transform: `translate(${xaxis1}px, 0px) translate3d(0px, 0px, 0px)` }}>1fahima farook1-</div>

        </div>
    );
}

export default InfiniteNameScroll;
