import React, { useEffect , useState} from 'react';

function BouncyButton(props) {
    const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
    const [buttonPosition, setButtonPosition] = useState({ x: 0, y: 0 });

    const handleMouseMove =(event)=>{
        const { clientX, clientY } = event;
        setMousePosition({ x: clientX, y: clientY });
        console.log(mousePosition.x, mousePosition.y)
    }

    const handleMouseEnter = ()=>{
        console.log("mouse touched");
        console.log(mousePosition.x, mousePosition.y)
    }

    const handleMouseLeave= ()=>{
        console.log("mouse left");
        console.log(mousePosition.x, mousePosition.y)
    }

    return (
        <div>
            <button className = "headerButton" onMouseMove={handleMouseMove} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave} >
                <p style={{transform: `translate(${mousePosition.x/10}%, ${mousePosition.y}%) rotate(0.001deg)`}}>{props.buttonName}</p>
            </button>   
        </div>
    );
}

export default BouncyButton;
