import React, { useEffect, useRef } from 'react';
import '../../assets/css/profilePage.css';
import '../../assets/css/helper.css';
import { useNavigate } from 'react-router-dom';

function Headerbar() {
    const handleScroll = () => {
        console.log('Scrolling...');
      };

    useEffect(()=>{
        window.addEventListener('scroll', handleScroll);

    return () => {
        window.removeEventListener('scroll', handleScroll);
        };

    },[])
    const navigate = useNavigate();
            const handleRedirect = (page) => {
            switch(page){
            case 'workPage':
                navigate('/work');
                break;
            case 'aboutPage':
                navigate('/about');
                break;
            case 'contactPage':
                navigate('/contact');
                break;
            }
    };

    return (
        <div>
        <div className = "headerButtonContainer">
            <button className = "headerButton" onClick={()=>handleRedirect("workPage")}>Work</button>
            <button className = "headerButton" onClick={()=>handleRedirect("aboutPage")}>About</button>
            <button className = "headerButton" onClick={()=>handleRedirect("contactPage")}>Contact</button>
        </div>
        </div>
    );
}

export default Headerbar;
