            <div className = "name-scroll name1" style={{transform: `translate(${xaxis}%, 0%) translate3d(0px, 0px, 0px)`}}>igloo is cute</div>
            <div className = "name-scroll name2" style={{transform: `translate(${xaxis}%, 0%) translate3d(0px, 0px, 0px)`}}>igloo is cute</div>
            <marquee scrollamount="10" direction = "left" className="scroll-name">igloo is cute</marquee>
            <div style ={{height:'1000px', width:'1000px'}}></div>
        </div>
    );
}

export default ProfilePage;
