import React, { useEffect, useRef, useState } from 'react';
import '../../assets/css/profilePage.css';
import '../../assets/css/helper.css';
import { useNavigate } from 'react-router-dom';

function Headerbar() {
    const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
    const[headerbar, setHeaderbar] = useState(true);
    const[headerIcon, setHeaderIcon] = useState(false);
    const headerTopValue =  useRef(null);

    const handleScroll = () => {
        if(headerTopValue.current.getBoundingClientRect().bottom > -100){ 
            setHeaderbar(true);
            setHeaderIcon(false);
        }
        else{
            console.log("here");
            setHeaderbar(false);
            setHeaderIcon(true);
        }
        console.log(headerTopValue.current.getBoundingClientRect().bottom, headerbar, headerIcon);
      };

    useEffect(()=>{
        window.addEventListener('scroll', handleScroll);
        headerTopValue.current = document.getElementsByClassName('headerButtonContainer')[0];

            return () => {
                window.removeEventListener('scroll', handleScroll);
            };

    },[])
    const navigate = useNavigate();
            const handleRedirect = (page) => {
            switch(page){
            case 'workPage':
                navigate('/work');
                break;
            case 'aboutPage':
                navigate('/about');
                break;
            case 'contactPage':
                navigate('/contact');
                break;
            }
    };
    const handleMouseMove =(event)=>{
        const { clientX, clientY } = event;
        setMousePosition({ x: clientX, y: clientY });
    }

    const handleMouseEnter = ()=>{
        console.log("mouse touched");
    }

    const handleMouseLeave= ()=>{
        console.log("mouse left");
    }

    return (
        <div>
        <div className = "header-icon"></div>
        <div className = "headerButtonContainer">
            { headerbar && <button className = "headerButton"onMouseMove={handleMouseMove} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave} onClick={()=>handleRedirect("workPage")}>Work</button>}
            {headerbar &&  <button className = "headerButton" onClick={()=>handleRedirect("aboutPage")}>About</button>}
            {headerbar &&  <button className = "headerButton" onClick={()=>handleRedirect("contactPage")}>Contact</button>}
            {headerIcon}
        </div>
        </div>
    );
}

export default Headerbar;
