import React, { useEffect, useState, useRef } from 'react';
import '../../assets/css/profilePage.css';

function InfiniteNameScroll(props) {

    const scrollString = props.scrollString;
    const[xaxis1, setXaxis1] = useState(0);
    const[xaxis2, setXaxis2] = useState(0);
    const slidingName1 =  useRef(null);
    const slidingName2 = useRef(null);
    const slidingNamePositions = useRef([slidingName1, slidingName2]);
    const nextStringAppended = useRef(false);


    useEffect(()=>{
        const setXaxis = ()=>{
            setXaxis1((prevCount)=>prevCount - 0.25)
            setXaxis2((prevCount)=>prevCount - 0.25)
            let screenWidth = window.innerWidth;
            if(screenWidth - slidingNamePositions.current[0].current.getBoundingClientRect().right > screenWidth*0.101){
                nextStringAppended.current = false;
                //console.log("0.2 gap --------")
            }
            else if (nextStringAppended.current != true && (screenWidth - slidingNamePositions.current[0].current.getBoundingClientRect().right > screenWidth*0.1 ))
            {
                console.log("0.1 gap ---")
                nextStringAppended.current = true;
                console.log(slidingName1.current.getBoundingClientRect().right);
                [slidingNamePositions.current[0], slidingNamePositions.current[1]] = [slidingNamePositions.current[1], slidingNamePositions.current[0]]
                console.log("slidingNamePositions" , slidingNamePositions);
            }
        }

        slidingName1.current = document.getElementsByClassName('name1')[0];
        slidingName2.current = document.getElementsByClassName('name2')[0];
        const intervalId = setInterval(setXaxis, 5);
    },[])


    return (
        <div className = "name-scroll-parent">
            <div className = "name-scroll name1" style={{transform: `translate(${xaxis1}px, 0px) translate3d(0px, 0px, 0px)`}}>fahima farook -</div>
            <div className = "name-scroll name2" style={{transform: `translate(${xaxis2}px, 0px) translate3d(0px, 0px, 0px)`}}>fahima farook -</div>
        </div>
    );
}

export default InfiniteNameScroll;


