import React, { useEffect, useRef, useState } from 'react';
import '../../assets/css/profilePage.css';
import '../../assets/css/helper.css';
import { useNavigate } from 'react-router-dom';
import BouncyButton from './BouncyButton'

function Headerbar() {

    const[headerbar, setHeaderbar] = useState(true);
    const[headerIcon, setHeaderIcon] = useState(false);
    const headerTopValue =  useRef(null);

    const handleScroll = () => {
        if(headerTopValue.current.getBoundingClientRect().bottom > -100.0){ 
            setHeaderbar((prev)=>!prev);
            setHeaderIcon((prev)=>!prev);
        }
        else{
            setHeaderbar((prev)=>!prev);
            setHeaderIcon((prev)=>!prev);
        }
        console.log(headerTopValue.current.getBoundingClientRect().bottom, headerbar, headerIcon);
      };

    useEffect(()=>{
        window.addEventListener('scroll', handleScroll);
        headerTopValue.current = document.getElementsByClassName('headerButtonContainer')[0];

            return () => {
                window.removeEventListener('scroll', handleScroll);
            };

    },[])
    const navigate = useNavigate();
            const handleRedirect = (page) => {
            switch(page){
            case 'workPage':
                navigate('/work');
                break;
            case 'aboutPage':
                navigate('/about');
                break;
            case 'contactPage':
                navigate('/contact');
                break;
            }
    };

    return (
        <div>
            <div className = "headerButtonContainer">
                {headerbar && <BouncyButton className = "headerButton working" onClick={()=>handleRedirect("workPage")} buttonName = 'working'/>}
                {headerbar &&  <BouncyButton className = "headerButton About" onClick={()=>handleRedirect("aboutPage")} buttonName = 'About'/>}
                {headerbar &&  <BouncyButton className = "headerButton Contact" onClick={()=>handleRedirect("contactPage")} buttonName = 'Contact'/>}
                {headerIcon && <div className='header-icon'></div>}
            </div>
        </div>
    );
}

export default Headerbar;
