import React, {useRef, useEffect, useState } from 'react';
import TrueTextColorChange from '../helper/TrueTextColorChange';
import '../../assets/css/profilePage.css'

function AboutMe(props) {

    const buttonNameRef = useRef(null);
    const [isVisible, setIsVisible] = useState(false);

    const observer = new IntersectionObserver((entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setIsVisible(true);
          } else {
            setIsVisible(false);
          }
        });
      });

    useEffect(()=>{

        if (buttonNameRef.current) {
            observer.observe(buttonNameRef.current);
        }

        return () => {
        if (buttonNameRef.current) {
            observer.unobserve(buttonNameRef.current);
        }
        };

    },[])
    return (
        <div className={`parent-tag ${isVisible ? 'change-the-background' : ''}`}>
            <div className='quote-1'> Every solution leads to a betterment</div>
            <div className= 'space-small'></div>
           <TrueTextColorChange/>
           <hr ref={buttonNameRef} style={{ width: 0, height: 0, border: 0}}></hr>
        </div>
    );
}

export default AboutMe;
