import React, {useState, useEffect} from 'react';

function MainPage() {
    const [counter, setCounter] = useState(0);

    useEffect(() => {
        const changeCounterValue =  () => {
            console.log(`counter is ${counter}`);
            setCounter(prev => prev + 1);
        }
        const intervalId = setInterval(changeCounterValue, 1000);

        return () => {
            clearInterval(intervalId);
          };

    }, [counter])

    const resetAction = ()=>{
        setCounter(0);
    }

    return (
        <div>
           <label className='counterText'>count : {counter} </label>
           <br></br>
           <button className = 'resetButton' onClick={resetAction}>reset</button>
        </div>
    );
}

export default MainPage;
